import { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState(null);
  const [view, setView] = useState(null);
  const [filter, setFilter] = useState('All');
  const [msg, setMsg] = useState('');

  const cats = ['All', 'Breakfast', 'Lunch', 'Dinner', 'Dessert', 'Snack', 'Other'];

  useEffect(() => {
    fetch('/api/recipes')
      .then(r => r.json())
      .then(data => {
        setRecipes(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  function showMsg(text) {
    setMsg(text);
    setTimeout(() => setMsg(''), 3000);
  }

  function add() {
    setForm({
      id: null,
      name: '',
      ingredients: '',
      instructions: '',
      prep: '',
      cook: '',
      servings: '',
      category: 'Other'
    });
    setView(null);
  }

  function edit(recipe) {
    setForm({ ...recipe });
    setView(null);
  }

  function cancel() {
    setForm(null);
  }

  function submit(e) {
    e.preventDefault();
    if (!form.name || !form.ingredients || !form.instructions) {
      showMsg('Fill required fields');
      return;
    }

    const method = form.id ? 'PUT' : 'POST';
    const url = form.id ? `/api/recipes/${form.id}` : '/api/recipes';

    fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    })
      .then(r => r.json())
      .then(data => {
        if (form.id) {
          setRecipes(recipes.map(r => r.id === data.id ? data : r));
          showMsg('Updated');
        } else {
          setRecipes([...recipes, data]);
          showMsg('Added');
        }
        setForm(null);
      });
  }

  function remove(id) {
    if (!window.confirm('Delete this recipe?')) return;

    fetch(`/api/recipes/${id}`, { method: 'DELETE' })
      .then(() => {
        setRecipes(recipes.filter(r => r.id !== id));
        setView(null);
        showMsg('Deleted');
      });
  }

  function open(recipe) {
    setView(recipe);
    setForm(null);
  }

  function close() {
    setView(null);
  }

  const filtered = filter === 'All' ? recipes : recipes.filter(r => r.category === filter);

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="app">
      <header>
        <h1>Recipe Manager</h1>
        <button onClick={add} className="btn-add">+ Add Recipe</button>
      </header>

      {msg && <div className="msg">{msg}</div>}

      {form && (
        <div className="modal">
          <div className="modal-content">
            <h2>{form.id ? 'Edit Recipe' : 'New Recipe'}</h2>
            <form onSubmit={submit}>
              <input
                placeholder="Recipe Name *"
                value={form.name}
                onChange={e => setForm({ ...form, name: e.target.value })}
              />
              <textarea
                placeholder="Ingredients (one per line) *"
                value={form.ingredients}
                onChange={e => setForm({ ...form, ingredients: e.target.value })}
                rows="5"
              />
              <textarea
                placeholder="Instructions *"
                value={form.instructions}
                onChange={e => setForm({ ...form, instructions: e.target.value })}
                rows="6"
              />
              <div className="row">
                <input
                  placeholder="Prep Time"
                  value={form.prep}
                  onChange={e => setForm({ ...form, prep: e.target.value })}
                />
                <input
                  placeholder="Cook Time"
                  value={form.cook}
                  onChange={e => setForm({ ...form, cook: e.target.value })}
                />
                <input
                  placeholder="Servings"
                  value={form.servings}
                  onChange={e => setForm({ ...form, servings: e.target.value })}
                />
              </div>
              <select
                value={form.category}
                onChange={e => setForm({ ...form, category: e.target.value })}
              >
                {cats.filter(c => c !== 'All').map(c => (
                  <option key={c} value={c}>{c}</option>
                ))}
              </select>
              <div className="actions">
                <button type="submit" className="btn-save">Save</button>
                <button type="button" onClick={cancel} className="btn-cancel">Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {view && (
        <div className="modal">
          <div className="modal-content view">
            <button onClick={close} className="close">√ó</button>
            <h2>{view.name}</h2>
            <div className="badge">{view.category}</div>
            <div className="meta">
              {view.prep && <span>Prep: {view.prep}</span>}
              {view.cook && <span>Cook: {view.cook}</span>}
              {view.servings && <span>Servings: {view.servings}</span>}
            </div>
            <h3>Ingredients</h3>
            <ul>
              {view.ingredients.split('\n').map((ing, i) => (
                <li key={i}>{ing}</li>
              ))}
            </ul>
            <h3>Instructions</h3>
            <p className="instructions">{view.instructions}</p>
            <div className="actions">
              <button onClick={() => edit(view)} className="btn-edit">Edit</button>
              <button onClick={() => remove(view.id)} className="btn-delete">Delete</button>
            </div>
          </div>
        </div>
      )}

      <div className="filters">
        {cats.map(c => (
          <button
            key={c}
            onClick={() => setFilter(c)}
            className={filter === c ? 'active' : ''}
          >
            {c}
          </button>
        ))}
      </div>

      {filtered.length === 0 ? (
        <div className="empty">
          <p>No recipes yet. Add your first one!</p>
        </div>
      ) : (
        <div className="grid">
          {filtered.map(recipe => (
            <div key={recipe.id} className="card" onClick={() => open(recipe)}>
              <h3>{recipe.name}</h3>
              <div className="badge">{recipe.category}</div>
              <div className="info">
                {recipe.prep && <span>‚è±Ô∏è {recipe.prep}</span>}
                {recipe.cook && <span>üî• {recipe.cook}</span>}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
